LED#int i = 0; void setup() { pinMode(25, OUTPUT); Serial.begin(9600); } void loop() { Serial.println(i); analogWrite(25, 50); Serial.println(&#34; Allume&#34;); delay(500); digitalWrite(25, 0); Serial.println(&#34; Eteint&#34;); delay(1000); i++; }
